import { useEffect, useState } from "react"
import { Row } from "react-bootstrap"
import moviesService from "../../services/movies.services"
import TopMovieList from "../../components/TopMovieList/TopMovieList"


const TopMoviePage = () => {
    const baseImageUrl = 'https://image.tmdb.org/t/p/original'

    const [movieData, setMovieData] = useState([])

    const [page, setPage] = useState(1)

    useEffect(() => {
        moviesFetch()
    }, [page])

    const moviesFetch = () => {
        return moviesService
            .getMovies()
            .then((res) => {
                const titles = res.data.results
                setMovieData(titles)
                return titles;
            })
            .catch((err) => console.log(err));
    }

    const handleNextPage = () => {
        setPage(prevPage => prevPage + 1)
    }

    return (
        <section>

            <div className='container'>

                <Row>
                    <TopMovieList moviesFetch={moviesFetch} movieData={movieData} handleNextPage={handleNextPage} />
                </Row>
            </div>

        </section>

    )
}

export default TopMoviePage
