import { useEffect, useState } from "react"
import { Col, Row } from "react-bootstrap"
import moviesService from "../../services/movies.services"
import TopMovieList from "../../components/TopMovieList/TopMovieList"


const TopMoviePage = () => {
    const baseImageUrl = 'https://image.tmdb.org/t/p/original'

    const [movieData, setMovieData] = useState([])

    const [page, setPage] = useState(1)

    useEffect(() => {
        moviesFetch()
    }, [page])

    const moviesFetch = () => {
        return moviesService
            .getTopRated()
            .then((res) => {
                const titles = res.data.results
                setMovieData(titles)
                return titles;
            })
            .catch((err) => console.log(err));
    }

    const handleNextPage = () => {
        setPage(prevPage => prevPage + 1)
    }

    return (
        <section>
            <div className='container'>
                <Row>
                    {movieData.map(elm => (
                        <Col md={{ span: 3 }} key={elm.id}>
                            <TopMovieCard className="movies" movie={elm} moviesFetch={moviesFetch} handleNextPage={handleNextPage} />
                        </Col>
                    ))}
                </Row>
                <button onClick={handleNextPage}>Load More</button>
            </div>
        </section>

    )
}

export default TopMoviePage
